import React from 'react';
import $ from 'jquery';

import '../Style/About.css';


class About extends React.Component {
    render(){

        $(document).ready(function() {
            var ProgressBar = require('progressbar.js')

            var circle = new ProgressBar.Circle('#progress', {
                color: '#FCB03C',
                duration: 3000,
                easing: 'easeInOut'
            });

            circle.animate(1);
        })

        return(
            <div className= "about-panel" id = "about">
                <div className= "row">
                <div className= "col-md-4 about-heading">
                    <div className= "orange-stripe"></div>
                    <h1 className= "portfolio-heading">About</h1>
                    <h5 className= "my-work">Education & Experience</h5>
                </div>

                    <div className= "col-md-6">
                        <p className= "about-para">
                            Hello,<br/><br/>
                            I am a student of Video Game Design Technologies in trioS college London, ON.  In this program I am learning video game concept development, video game analysis and technical design, drawing and Storyboarding, vector art, pixel art, 3d modelling, advanced animation, advanced unity game development and level design.
                            I thrive in creative and collaborative environments where hard work and passion for making great game is valued, and where I can leverage my strong technical background to enhance my designs and team. My career goals are to continue to learn and grow as a great designer.
                            <br/><br/>
                            “A creative man is motivated by the desire to achieve, not by the desire to beat others.” –            Ayn Rand
                        </p>

                        <div id="progress"></div>

                    </div>
                </div>
            </div>
        )
    }
}

export default About;
